import { useEffect,useRef } from 'react';
import './App.css';
import $ from 'jquery'

function App() {

	// const welcomeTitle={
		// transform:'translate(-50%,-50%)',
		// top:'50%',
		// left:'50%',
		// position:'absolute',
	// }
	const startQuiz=useRef()

	useEffect(()=>{
		const bodyHeight=document.documentElement.scrollHeight;
		console.log((bodyHeight));
		$('#bgImage').css({height:bodyHeight})
	},[])

	return(
		<div className='bg-dark' id='bgImage'>
			<div className='text-center text-light' id='welcomeTitle'>
				<h1 className='display-3' id='onlinequiz'>Online Quizz</h1>
				<h6 className='text-wrap'>Quick Quiz knowledge Booster for a Perfect Level Up</h6>
				<hr className='text-light'></hr>
				<h1 className='display-5' id='welcome'>Welcome!</h1>
				<button type='button' ref={startQuiz} className='btn btn-outline-success px-5 py-2 mt-5'>Start<span></span></button>
			</div>
		</div>
	);
}

export default App;
