import axios from 'axios'
import React, { useEffect, useState } from 'react'

function QuizPage(props) {

    const [data,setData]=useState([])

    const [questions,setQuestions]=useState([])

    let questionCount=0

    // const [uName]=props.userName;
    const quizApi='https://quizapi.io/api/v1/questions?apiKey=P7mSuG2QyMCPnS5zWo3iYBVLH61UG1M7OMt1SDHf&category=code&difficulty=Easy&limit=20&tags=JavaScript'

    useEffect(()=>{
        fetchApi()
    },[])

    const fetchApi=async()=>{
        console.log('fetching');
        try{
            const responseApi=await axios.get(quizApi)
            console.log(responseApi.data)
            setData(responseApi.data)
        }
        catch(err){
            console.log('Network error');
            alert('Network error');
        }

        addQuestions()
    }

    const addQuestions = ()=>{

        

    }

    return (
        <div>
            <div className='d-flex justify-content-between'>
                <h4 className='text-start ms-4 mt-2'>Laxmana Rao</h4>
                <h4 className='text-end me-4 mt-2'>10:00</h4>
            </div>
            {
                
                data.map((item,index)=>{
                    // setQuestionCount(questionCount+1)
                    // return <div key={index}>{item.question}</div>
                    const question= item.question;
                    questionCount=questionCount+1

                    return <ol type='1' start={questionCount}>
                                <li>{question}</li>
                                    <input 
                                        type='radio' 
                                        name='option1' 
                                        id='option1'
                                    />
                                    <label for='option1'>{item.answers.answer_a}</label>
                    <br/>
                                    <input 
                                        type='radio' 
                                        name='option2' 
                                        id='option2'
                                    />
                                    <label for='option2'>{item.answers.answer_b}</label>
                                    
                                    <input 
                                        type='radio' 
                                        name='option3' 
                                        id='option3'
                                    />
                                    <label for='option3'>{item.answers.answer_c}</label>
                                    
                                    <input 
                                        type='radio' 
                                        name='option4' 
                                        id='option4'
                                    />
                                    <label for='option4'>{item.answers.answer_d}</label>
                            </ol>
                    
                }
            )
            }
        </div>
    )
}

export default QuizPage
