import axios from 'axios'
import React, { useEffect, useState } from 'react'

function QuizPage(props) {

    const [data,setData]=useState([])

    const [userInp,setUserInp]=useState([])

    let questionCount=1

    // const [uName]=props.userName;
    const quizApi='https://quizapi.io/api/v1/questions?apiKey=P7mSuG2QyMCPnS5zWo3iYBVLH61UG1M7OMt1SDHf&category=code&difficulty=Easy&limit=20&tags=JavaScript'

    useEffect(()=>{
        fetchApi()
    },[])

    const fetchApi=async()=>{
        console.log('fetching');
        try{
            const responseApi=await axios.get(quizApi)
            console.log(responseApi.data)
            setData(responseApi.data)
        }
        catch(err){
            console.log('Network error');
            alert('Network error');
        }

        addQuestions()
    }

    const addQuestions = (item,optionNum,char)=>{

        return <>
            <input
                type='radio'
                name='options'
                value={item}
                onChange={(e)=>e.target.checked}
                id={`qtn${questionCount}option${optionNum}`}
            />
            <label for={`qtn${questionCount}option${optionNum}`}>{item}</label>
        </>

    }

    return (
        <div>
            <div className='d-flex justify-content-between'>
                <h4 className='text-start ms-4 mt-2'>Laxmana Rao</h4>
                <h4 className='text-end me-4 mt-2'>10:00</h4>
            </div>
            <ol type='1' start={questionCount}>
                {
                    
                    data.map((item,index)=>{
                        // setQuestionCount(questionCount+1)
                        // return <div key={index}>{item.question}</div>
                        const question= item.question;
                        questionCount=questionCount+1

                        let opt1=(item.answers.answer_a!==null)?item.answers.answer_a:''
                        let opt2=(item.answers.answer_b!==null)?item.answers.answer_a:''
                        let opt3=(item.answers.answer_c!==null)?item.answers.answer_a:''
                        let opt4=(item.answers.answer_d!==null)?item.answers.answer_a:''
                        let opt5=(item.answers.answer_e!==null)?item.answers.answer_a:''
                        let opt6=(item.answers.answer_f!==null)?item.answers.answer_a:''

                        return <form key={index}>
                                    <li id={`question${questionCount}`}>{question}</li>
                                        {
                                            (item.answers.answer_a)?addQuestions(item.answers.answer_a,1,'a'):''
                                        }
                                    <br/>
                                        {
                                            (opt2)?addQuestions(item.answers.answer_b,2,'b'):''
                                        }
                                    <br/>
                                        {
                                            (opt3)?addQuestions(item.answers.answer_c,3,'c'):''
                                        }
                                    <br/>
                                        {
                                            (opt4)?addQuestions(item.answers.answer_d,4,'d'):''
                                        }
                                    <br/>
                                        {
                                            (opt5)?addQuestions(item.answers.answer_e,5,'e'):''
                                        }
                                    <br/>
                                        {
                                            (opt6)?addQuestions(item.answers.answer_f,6,'f'):''
                                        }
                                </form>
                        
                    })
                }
            </ol>
            {
                userInp.map((user) =>{return <h1>{user}</h1>})
            }
        </div>
    )
}

export default QuizPage
