/* eslint-disable jsx-a11y/anchor-is-valid */
import axios from 'axios'
import React, { useEffect, useState } from 'react'
import $ from 'jquery'

function QuizPage(props) {

    const [data,setData]=useState([])

    const [userAnswers,setUserAnswers]=useState([])

    // define the count Interval for the countdown timer :)
    let countInterval

    // count the number of questions, which helps us to allocate id's to the question :)
    let questionCount=0

    // const [uName]=props.userName;
    const quizApi='https://quizapi.io/api/v1/questions?apiKey=P7mSuG2QyMCPnS5zWo3iYBVLH61UG1M7OMt1SDHf&category=code&difficulty=Easy&limit=20&tags=JavaScript'

    useEffect(()=>{
        console.log('useeffect');

        // call fetchApi function when component renders everytime :)
        fetchApi()

        // call countDown function when component(quizPage) renders everytime :)
        countDown()

        window.addEventListener('scroll',()=>{
            const scrollHeight=document.documentElement.scrollTop
            if(scrollHeight<320)
                $('#scrollToTop').css({display:'none'})
            else
                $('#scrollToTop').css({display:'block'})

            
        })
        // const promotionbtn=document.querySelector('#promotionBtn')
        // promotionbtn.addEventListener('mousedown',()=>{
        //     $('#promotion').css({display:'block'})
        //     $('#promotion').addClass('d-flex')
        // })
        // promotionbtn.addEventListener('mouseup',()=>{
        //     $('#promotion').css({display:'none'})
        //     $('#promotion').removeClass('d-flex')
        // })

        return ()=>{
            
            // We have to clear the setInterval when the Component is destroyed :)
            clearInterval(countInterval)
        }

    },[])

    const mousedownFunc=()=>{
        $('#promotion').css({display:'block'})
        $('#promotion').addClass('d-flex')
    }

    const mouseupFunc=()=>{
        $('#promotion').css({display:'none'})
        $('#promotion').removeClass('d-flex')
    }

    // Fetch the API data using axios and asynchronous method :)
    const fetchApi=async()=>{

        console.log('fetching');
        try{
            const responseApi=await axios.get(quizApi)
            // console.log(responseApi.data)
            setData(responseApi.data)
        }
        catch(err){
            console.log('Network error');
            alert('Network error');
        }

    }

    // set the coundown time to the user :)
    const countDown=()=>{
        const countDownEle=document.querySelector('div #countDown')
        let seconds=0
        let minutes=0

        countInterval=setInterval(()=>{
            if(seconds>=60){
                minutes++
                seconds=0
            }

            /* 4 constraints
            *   seconds<10 -> minutes>9 & minutes<10
            *   seconds>10 -> minutes>9 & minutes<10
            */
            countDownEle.textContent=(seconds<10)?   
                                                ((minutes>9)? minutes+":0"+seconds   :   "0"+minutes+":0"+seconds )  
                                                :   
                                                (minutes>9)? minutes+":"+seconds   :   "0"+minutes+":"+seconds

            seconds++
        },1000)

    }

    // rendering questions to the browser :)
    const addQuestions = (option,optionNum)=>{ // it accepts 2 parameters -> option value(label) & -> option number :)

        return  <div className='rounded rounded-3 py-2 px-2 mb-4 mt-1 optionParent'>
                    {/*  render input (radio) elements */}
                    <input
                        type='radio'
                        name='options'
                        className='form-check-input ms-3 option'
                        value={option}
                        onChange={(e)=>checkInputEle(e)}
                        id={`qtn${questionCount}option${optionNum}`}
                    />
                    <label className='ms-2' htmlFor={`qtn${questionCount}option${optionNum}`}>{option}</label>
                </div>

    }

    const checkInputEle=(e)=>{
        // const questionNum=e.target.id;
        // const n=String(questionNum.substring(0,4))+""
        
        setUserAnswers([...userAnswers,e.target.id])

        console.log(userAnswers);

        return e.target.checked
    }
     
    return (
        <>
            <div className='d-flex flex-wrap justify-content-between sticky-top py-2'>
                <h4 className='ms-4'>Quizz</h4>
                <div className='d-flex align-items-center'>
                    <i className="fa-regular fa-clock me-2 h5"></i>
                    <h4 className='text-center' id='countDown'>00:00</h4>
                </div>
                <h4 className='text-end me-4'>laxmanarao2003</h4>
                
            </div>
            <div className='d-flex justify-content-center mt-4'>
                <div className='col-11 col-xxl-5 col-xl-6 col-lg-7 col-md-8' id='title'>
                    <h3 className='py-4 px-4'>Online test</h3>
                </div>
            </div>
            <div className='d-flex justify-content-center mt-3'>
                <div className='col-11 col-xxl-5 col-xl-6 col-lg-7 col-md-8 pb-3'>

                    {   
                        data.map((item,index)=>{

                            const question= item.question
                            questionCount=questionCount+1

                            return <div key={index} className='rounded rounded-4 px-4 mt-3 mb-4 py-4' id='borderEle'>
                                        <div className='text-start rounded rounded-4 mb-4 mt-2 questionParent'>
                                            <p id={`question${questionCount}`} className='question py-2 px-3'>{questionCount}. {question}<sup> *</sup></p>
                                        </div>
                                        <div className='d-flex justify-content-between flex-column'>

                                            {/* check how many options that are available to each question */}
                                            {
                                                (item.answers.answer_a)?addQuestions(item.answers.answer_a,1):''
                                            }
                                            {
                                                (item.answers.answer_b)?addQuestions(item.answers.answer_b,2):''
                                            }
                                    
                                            {
                                                (item.answers.answer_c)?addQuestions(item.answers.answer_c,3):''
                                            }
                                        
                                            {
                                                (item.answers.answer_d)?addQuestions(item.answers.answer_d,4):''
                                            }
                                            {
                                                (item.answers.answer_e)?addQuestions(item.answers.answer_e,5):''
                                            }
                                            {
                                                (item.answers.answer_f)?addQuestions(item.answers.answer_f,6):''
                                            }

                                        </div>
                                    </div>
                            
                        })
                    }

                </div>
            </div>
            
            <div className='text-center my-5'>
                <button className='btn btn-primary px-4 py-2'>Submit</button>
            </div>

            {/* Navigate (Scroll) to top Button */}
            <div className='text-end fixed-bottom me-3 mb-3' id='scrollToTop'>
                <a href='#' className='btn btn-outline-primary'><i class="fa-solid fa-angles-up"></i></a>
            </div>
            
            {/* Promotion Buttons */}

            <div className='fixed-bottom ms-2 mb-3' onMouseDown={mousedownFunc} onMouseOut={mousedownFunc} id='promotionBtn'>
                    <a href='#' className='btn btn-outline-primary'><i class="fa-solid fa-right-to-bracket"></i></a>
            </div>
            <div className='flex-column fixed-bottom ms-2 mb-3' id='promotion'>
                <div className='mb-2'>
                    <a href='https://github.com/laxmanarao2003' target='blank' className='btn btn-outline-primary'><i class="fa-brands fa-github"></i></a>
                </div>
                <div className='mb-2'>
                    <a href='https://www.instagram.com/laxmanarao_arasavilli/' target='blank' className='btn btn-outline-primary'><i class="fa-brands fa-instagram"></i></a>
                </div>
                <div className='mb-2'>
                    <a href='https://www.facebook.com/laxmanarao2003/' target='blank' className='btn btn-outline-primary'><i class="fa-brands fa-facebook"></i></a>
                </div>
                <div>
                    <a href='https://twitter.com/laxmanarao2003' target='blank' className='btn btn-outline-primary'><i class="fa-brands fa-twitter"></i></a>
                </div>
            </div>



            {/* {
                userAnswers.map((user,index) =>{return <h1 key={index}>{user}</h1>})
            } */}
        </>
    )
}

export default QuizPage
