import { useEffect } from 'react';
import './App.css';
import $ from 'jquery'

function App() {

	// const welcomeTitle={
		// transform:'translate(-50%,-50%)',
		// top:'50%',
		// left:'50%',
		// position:'absolute',
	// }

	useEffect(()=>{
		const bodyHeight=document.documentElement.scrollHeight;
		console.log((bodyHeight));
		$('#bgImage').css({height:bodyHeight})
	},[])

	return(
		<div className='bg-dark' id='bgImage'>
			<div className='container d-flex align-items-center' id='welcomeTitle'>
				<img className='rounded-circle' src='https://png.pngtree.com/png-vector/20220621/ourmid/pngtree-3dstyle-purple-quiz-banner-on-white-background-illustrated-in-vector-format-vector-png-image_47195826.jpg'/>
				<div className='text-center text-light'>
					<h1 className='display-3' id='onlinequiz'>Online Quizz</h1>
					<h6>Quick Quiz knowledge Booster for a Perfect Level Up</h6>
					<hr className='text-light'></hr>
					<h1 className='display-5' id='welcome'>Welcome!</h1>
				</div>
			</div>
		</div>
	);
}

export default App;
