/* eslint-disable jsx-a11y/anchor-is-valid */
import axios from 'axios'
import React, { useEffect, useRef, useState } from 'react'
import $ from 'jquery'

function QuizPage(props) {

    const [data,setData]=useState([])

    const [userAnswers,setUserAnswers]=useState([])

    // define the count Interval for the countdown timer :)
    let countInterval

    // count the number of questions, which helps us to allocate id's to the question :)
    let questionCount=0

    // create useRef Hook for promotion, which stores the mutable value even if the component re-renders:)
    let btnClicked=useRef()

    // const [uName]=props.userName;
    const quizApi='https://quizapi.io/api/v1/questions?apiKey=P7mSuG2QyMCPnS5zWo3iYBVLH61UG1M7OMt1SDHf&category=code&difficulty=Easy&limit=20&tags=JavaScript'

    useEffect(()=>{
        console.log('useeffect');

        // call fetchApi function when component renders everytime :)
        fetchApi()

        // call countDown function when component(quizPage) renders everytime :)
        countDown()

        btnClicked.current=true

        window.addEventListener('scroll',()=>{
            const scrollHeight=document.documentElement.scrollTop
            if(scrollHeight<320)
                $('#scrollToTop').css({display:'none'})
            else
                $('#scrollToTop').css({display:'block'})

            
        })

        // when clicking on the promotion button it will toggle the social media buttons :)
        const promotionbtn=document.querySelector('#promotionBtn')
        promotionbtn.addEventListener('click',()=>{
            if(btnClicked.current===true){
                $('#promotion').css({display:'block'})
                $('#promotion').addClass('d-flex')
                btnClicked.current=false
            }
            else{
                $('#promotion').css({display:'none'})
                $('#promotion').removeClass('d-flex')
                btnClicked.current=true
            }
        })

        return ()=>{
            
            // We have to clear the setInterval when the Component is destroyed :)
            clearInterval(countInterval)
        }

    },[])

    // Fetch the API data using axios and asynchronous method :)
    const fetchApi=async()=>{

        console.log('fetching');
        try{
            const responseApi=await axios.get(quizApi)
            // console.log(responseApi.data)
            setData(responseApi.data)
        }
        catch(err){
            console.log('Network error');
            alert('Network error');
        }

    }

    // set the coundown time to the user :)
    const countDown=()=>{
        const countDownEle=document.querySelector('div #countDown')
        let seconds=0
        let minutes=0

        countInterval=setInterval(()=>{
            if(seconds>=60){
                minutes++
                seconds=0
            }

            /* 4 constraints
            *   seconds<10 -> minutes>9 & minutes<10
            *   seconds>10 -> minutes>9 & minutes<10
            */
            countDownEle.textContent=(seconds<10)?   
                                                ((minutes>9)? minutes+":0"+seconds   :   "0"+minutes+":0"+seconds )  
                                                :   
                                                (minutes>9)? minutes+":"+seconds   :   "0"+minutes+":"+seconds

            seconds++
        },1000)

    }

    // rendering questions to the browser :)
    const addQuestions = (option,optionNum)=>{ // it accepts 2 parameters -> option value(label) & -> option number :)

        return  <div className='rounded rounded-3 py-2 px-2 mb-4 mt-1 optionParent'>
                    {/*  render input (radio) elements */}
                    <input
                        type='radio'
                        name='options'
                        className='form-check-input ms-3 option'
                        value={option}
                        onChange={(e)=>checkInputEle(e)}
                        id={`qtn${questionCount}option${optionNum}`}
                    />
                    <label className='ms-2' htmlFor={`qtn${questionCount}option${optionNum}`}>{option}</label>
                </div>

    }

    const checkInputEle=(e)=>{
        // const questionNum=e.target.id;
        // const n=String(questionNum.substring(0,4))+""
        
        setUserAnswers([...userAnswers,e.target.id])

        console.log(userAnswers);

        return e.target.checked
    }
     
    return (
        <>
            <div className='d-flex flex-wrap justify-content-between sticky-top py-2'>
                <h4 className='ms-4'>Quizz</h4>
                <div className='d-flex align-items-center'>
                    <i className="fa-regular fa-clock me-2 h5"></i>
                    <h4 className='text-center' id='countDown'>00:00</h4>
                </div>
                <h4 className='text-end me-4'>Laxmana Rao</h4>
                
            </div>
            <div className='d-flex justify-content-center mt-4'>
                <div className='col-9 col-xxl-5 col-xl-6 col-lg-7 col-md-8' id='title'>
                    <h3 className='py-3 px-4 my-2 me-2'>Online test <button className='btn btn-primary rounded rounded-4 my-2' data-bs-toggle="modal" data-bs-target="#exampleModal">instructions<i className="fa-solid fa-clipboard ms-2" title='instructions'></i></button></h3>
                </div>
            </div>

            {/* <!-- Button trigger modal --> */}
            {/* <!-- instructions Modal --> */}
            <div className="modal fade" id="exampleModal" tabIndex="-1">
                <div className="modal-dialog modal-dialog-centered modal-dialog-scrollable modal-fullscreen">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Instructions</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                    <div className="modal-body">
                        <div className="bg-light" id="instructions">
                            <div className="container pt-2">
                                <h3 className="text-danger py-3">Quizz - General Instructions</h3>
                                <ol>
                                    <li className='mb-3'>
                                        Starting the Quiz: Click the "Start Quiz" button when you're ready to begin. The timer will start once you enter the quiz.
                                    </li>
                                    <li className='my-3'>
                                        Answering Questions:
                                        <ul>
                                            <li className='my-3'>You'll be presented with a series of questions one at a time.</li>
                                            <li className='my-3'>Select your answers by clicking on the choices provided.</li>
                                        </ul>
                                    </li>
                                    <li className='my-3'>
                                        Rules and Time Limit: 
                                        <ul>
                                            <li className='my-3'>Read the quiz rules and the time limit for the quiz.</li>
                                            <li className='my-3'>To conduct the exam in fair means, we are defining maximum time limit for each of the multiple-choice questions</li>
                                            <li className='my-3'>The clock will be set at the server. The countdown timer in the top right corner of screen will display the time remaining for each question.</li>
                                        </ul>
                                    </li>
                                    <li className='my-3'>
                                        Review and Submit: 
                                        <ul>
                                            <li className='my-3'>Before submitting your quiz, review your answers.</li>
                                            <li className='my-3'>Once you're satisfied, click the "Submit" button.</li>
                                        </ul>
                                        
                                    </li>
                                    <li className='my-3'>
                                        Scoring: Your score will be calculated automatically, and you'll see your results immediately after submission.
                                    </li>
                                    <li className='my-3'>
                                        Results: Take note of your score 
                                    </li>
                                    <li className='my-3'>
                                        Respecting Guidelines: Please adhere to the platform's terms of use and guidelines, and ensure fair play.
                                    </li>
                                    <li className='my-3'>
                                        Enjoy and Have Fun!: Remember, quizzes are not just about testing your knowledge but also about learning. Have fun and enjoy the challenge!
                                    </li>
                                    <li className='my-3'>
                                        For multiple Type choice question
                                        <ol className="" type='a'>
                                            <li className='my-3'>To select your answer you click one of the options buttons</li>
                                            <li className='my-3'>To change an answer to a question,click the another desired option button</li>
                                        </ol>
                                    </li>
                                    <li className='my-3'>Zoom level should be 100%</li>
                                    <li className='my-3'>Allow the pop-up Windows</li>
                                    <li className='my-3'>open in Browser IE8 or above/FireFox/Chrome </li>
                                </ol>
                            </div>
                        </div>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                    </div>
                    </div>
                </div>
            </div>
            {/* <!-- instructions Modal --> */}

            <div className='d-flex justify-content-center mt-3'>
                <div className='col-9 col-xxl-5 col-xl-6 col-lg-7 col-md-8 pb-3'>

                    {   
                        data.map((item,index)=>{

                            const question= item.question
                            questionCount=questionCount+1

                            return <div key={index} className='rounded rounded-4 px-4 mt-3 mb-4 py-4' id='borderEle'>
                                        <div className='text-start rounded rounded-4 mb-4 mt-2 questionParent'>
                                            <p id={`question${questionCount}`} className='question py-2 px-3'>{questionCount}. {question}<sup> *</sup></p>
                                        </div>
                                        <div className='d-flex justify-content-between flex-column'>

                                            {/* check how many options that are available to each question */}
                                            {
                                                (item.answers.answer_a)?addQuestions(item.answers.answer_a,1):''
                                            }
                                            {
                                                (item.answers.answer_b)?addQuestions(item.answers.answer_b,2):''
                                            }
                                    
                                            {
                                                (item.answers.answer_c)?addQuestions(item.answers.answer_c,3):''
                                            }
                                        
                                            {
                                                (item.answers.answer_d)?addQuestions(item.answers.answer_d,4):''
                                            }
                                            {
                                                (item.answers.answer_e)?addQuestions(item.answers.answer_e,5):''
                                            }
                                            {
                                                (item.answers.answer_f)?addQuestions(item.answers.answer_f,6):''
                                            }

                                        </div>
                                    </div>
                            
                        })
                    }

                </div>
            </div>
            
            <div className='text-center my-5'>
                <button className='btn btn-primary px-4 py-2'>Submit</button>
            </div>

            {/* Navigate (Scroll) to top Button */}
            <div className='text-end fixed-bottom me-3 mb-3' id='scrollToTop'>
                <a href='#' className='btn btn-outline-primary'><i className="fa-solid fa-angles-up"></i></a>
            </div>
            
            {/* Promotion Buttons */}
            <div className='fixed-bottom ms-2 mb-1 mt-2' style={{width:'35px'}}>
                <button className='btn btn-outline-primary' id='promotionBtn'><i className="fa-solid fa-right-to-bracket"></i></button>
            </div>

            <div className='justify-content-start flex-column fixed-bottom ms-2 mb-5' style={{width:'35px',display:'none'}} id='promotion'>
                <div className='mb-2'>
                    <a href='https://github.com/laxmanarao2003' target='blank' className='btn btn-outline-primary'><i className="fa-brands fa-github"></i></a>
                </div>
                <div className='mb-2'>
                    <a href='https://www.instagram.com/laxmanarao_arasavilli/' target='blank' className='btn btn-outline-primary'><i className="fa-brands fa-instagram"></i></a>
                </div>
                <div className='mb-2'>
                    <a href='https://www.facebook.com/laxmanarao2003/' target='blank' className='btn btn-outline-primary'><i className="fa-brands fa-facebook"></i></a>
                </div>
                <div>
                    <a href='https://twitter.com/laxmanarao2003' target='blank' className='btn btn-outline-primary'><i className="fa-brands fa-twitter"></i></a>
                </div>
            </div>



            {/* {
                userAnswers.map((user,index) =>{return <h1 key={index}>{user}</h1>})
            } */}
        </>
    )
}

export default QuizPage
